<template>
  <div class="collect_container">
    <!-- 头部部分 -->
    <div class="collect_top_container">
      <div>
        <img
          @click="backClick"
          style="width:18px;height:20px;"
          src="data:image/png;base64,"
          alt
        />
      </div>
      <div class="collect_top_title">{{this.$route.meta.title}}</div>
      <div>
        <img
          @click="goSearchClick"
          style="width:18px;height:20px;"
          src="data:image/png;base64,"
          alt
        />
      </div>
    </div>
    <!-- 筛选部分 -->
    <div class="collect_select_container">
      <div :class="['collect_left',{'collect_left_active':isShow}]" @click="onClick">
        <div>类型不限</div>
        <van-icon :class="['collect_vant',{'collect_vant_active':isShow}]" name="play" />
      </div>
    </div>
    <!-- 提示框部分 -->
    <div class="collect_confirm_container" v-show="isShow">
      <div
        :class="['collect_confirm_item',{'collect_confirm_item_active':selIndex==index}]"
        @click="confirmClick(index)"
        v-for="(item,index) in collectData"
        :key="index"
      >{{item}}</div>
    </div>
    <!-- 蒙层部分 -->
    <div class="meng_container" v-show="isShow" @click="closeClick"></div>
    <!-- empty部分 -->
    <div class="collect_logo_container">
      <img src="../../assets/empty.png" alt />
      <p>暂无错题记录</p>
    </div>
  </div>
</template>

<script>
export default {
  components: {},
  data() {
    return {
      isShow: false,
      selIndex: 0,
      collectData: ["单选", "多选", "判断", "连线"]
    };
  },
  computed: {},
  methods: {
    onClick() {
      this.isShow = !this.isShow;
    },
    confirmClick(index) {
      this.selIndex = index;
    },
    backClick() {
      window.history.back();
    },
    closeClick() {
      this.isShow = false;
    },
    goSearchClick() {
      this.$router.push("/searchs");
    }
  },
  mounted() {}
};
</script>
